tus='Closed'";			$resultClosedQuiz = resultQuery($closedQuiz,$conn);							echo "<table id='table1'>";			echo "<tr><td><b>Quiz ID</b></td><td><b>Quiz Title</b></td><td><b>Total Marks</b></td><td><b>No of Questions</b></td><td><b>Quiztype</b></td><td><b>Timelimit</b></td><td></td></tr>";						for($i=0;$i<count($resultClosedQuiz);$i++)			{				echo "<tr>";								for($j=0;$j<6;$j++)				{					echo "<td>";										if($j==4)					{						if($resultClosedQuiz[$i][$j]==1)							echo "Objective";												else if ($resultClosedQuiz[$i][$j]==2)							echo "Subjective";												else							echo "Mixed";					}										else						echo $resultClosedQuiz[$i][$j]; 											echo "</td>";				}								$quizid = $resultClosedQuiz[$i][0];				echo "<td>";				echo "<a href=editquiz.php?quizid=$quizid>Edit Quiz</a>";				echo "</td>";				echo "</tr>";			}		}		else		{			echo "<p>No closed quizzes</p>";		}		?></table></div>	<div id="footer"><p>footer here: &copy; 2009 <a href="#" title="Your Company">Your Company</a> | Privacy | Terms of Use</p></div></div></body></html>