<?phpfunction getQuizID($count){	if($count<10)	{		$quizID = "QUIZ00".$count;	}	else if($count>9)	{		$quizID = "QUIZ0".$count;	}	else if($count>99)	{		$quizID = "QUIZ".$count;	}		return $quizID;}// FUNCTION FOR SHUFFLING IF NO OF QUESTIONS ADDED IS THE SAME AS QUESTIONS REQUIREDfunction randomize($max){	$numberArray = array();	$numbers = range(0, $max);	shuffle($numbers);	foreach ($numbers as $number) 	{		$numberArray[] = $number;	}		return $numberArray;}function randomizeTwo($max, $count){	$numberArray = array();		function random_range($min, $max, $count)	{		$count = abs((int)$count);		if($min > $max){			list($min, $max) = array($max, $min);		}		$uniques = abs($max - $min);		if($count > $uniques)$count = $uniques;		$result = range($min, $max);		shuffle($result);		return array_slice($result, 0, $count);	}	foreach(random_range(0, $max, $count) as $random){		$numberArray[] = $random;	}		return $numberArray;}?>